import React from 'react'
import PropTypes from 'prop-types'
import voteUp from "../images/vote-up.png";

const SideBar = ({ handleVote, fakeVotes, loggedInUser }) => {
    return (
        <div className="side_bar">
            <button className="vote_button" disabled={!loggedInUser ? true : false} onClick={() => { handleVote('up') }}><span aria-label="thumbs up" role="img"><img src={voteUp} height="5px" alt=""/></span></button>
            <p className="vote_count">{fakeVotes}</p>
            <button className="vote_button" disabled={!loggedInUser ? true : false} onClick={() => { handleVote('down') }}><span aria-label="thumbs up" role="img">ðŸ‘Ž</span></button>
        </div>
    )
}

SideBar.propTypes = {
    handleVote: PropTypes.func.isRequired,
    fakeVotes: PropTypes.number.isRequired,
    loggedInUser: PropTypes.object
}

export default SideBar
